// api url: api.openweathermap.org/data/2.5/weather?q={city name},{state code}&appid={API key}
// api key: 5ce4087d015baa18617d85c961cefaa8
import './weather.css'
import axios from "axios";
import { useEffect , useState} from "react";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {faSun, faMoon, faCloudSun, faSmog, faSnowflake, faCloud, faCloudRain} from '@fortawesome/free-solid-svg-icons'

function Weather(){

    const [temp, setTemp] = useState(0)
    const [weatherDescriptor, setWeatherDescriptor] = useState("Sunny")
    const [weatherCode, setWeatherCode] = useState('01d')

    function getIcon(){
        switch(weatherCode){
            case "01d": return(<FontAwesomeIcon icon={faSun} className="fa-5x"/>); //Clear Day
            case "01n": return(<FontAwesomeIcon icon={faSun} className="fa-5x"/>); //Clear Night
            case "02d": return(<FontAwesomeIcon icon={faCloudSun} className="fa-5x"/>); //Partly Cloudy Day
            case "02n": return(<FontAwesomeIcon icon={faSun} className="fa-5x"/>); //Partly Cloudy Night
            case "03d": return(<FontAwesomeIcon icon={faSun} className="fa-5x"/>); //Overcast Day
            case "03n": return(<FontAwesomeIcon icon={faSun} className="fa-5x"/>); //Overcast Night
            case "04d": return(<FontAwesomeIcon icon={faSun} className="fa-5x"/>); //Overcast Day 2
            case "04n": return(<FontAwesomeIcon icon={faSun} className="fa-5x"/>); //Overcast Night 2
            case "09d": return(<FontAwesomeIcon icon={faSun} className="fa-5x"/>); //Rain Shower Day
            case "09n": return(<FontAwesomeIcon icon={faSun} className="fa-5x"/>); //Rain Shower Night
            case "10d": return(<FontAwesomeIcon icon={faSun} className="fa-5x"/>); //Rain Day
            case "10n": return(<FontAwesomeIcon icon={faSun} className="fa-5x"/>); //Rain Night
            case "11d": return(<FontAwesomeIcon icon={faSun} className="fa-5x"/>); //ThunderStorm Day
            case "11n": return(<FontAwesomeIcon icon={faSun} className="fa-5x"/>); //ThunderStorm Night
            case "13d": return(<FontAwesomeIcon icon={faSun} className="fa-5x"/>); //Snow Day
            case "13n": return(<FontAwesomeIcon icon={faSun} className="fa-5x"/>); //Snow Night
            case "50d": return(<FontAwesomeIcon icon={faSmog} className="fa-5x"/>); //Mist Day
            case "50n": return(<FontAwesomeIcon icon={faSmog} className="fa-5x"/>); //Mist Night
            default: return
        }

    }

    useEffect(() => {
        setInterval(()=>{
            axios.get("https://api.openweathermap.org/data/2.5/weather?lat=39.4808&lon=-106.0676&appid=5ce4087d015baa18617d85c961cefaa8&units=imperial").then((response) => {
            // console.log(response.data)
            setTemp(Math.round(response.data.main.temp))
            // console.log(response.data.main.temp)
            setWeatherDescriptor(response.data.weather[0].main)
            setWeatherCode(response.data.weather[0].icon)
            console.log("Weather Updated!")
        });
        }, 30000)

    });


    return(
        <div className="Weather">
            <div>
                <span className="temp"> {temp}<span>&deg;</span></span>
                <span className="weatherType">{weatherDescriptor}</span>
            </div>
            {getIcon()}
        </div>
    );
}

export default Weather;
